// Parse Beaumont CVB Events
const html = $input.item.json.data;
const events = [];

// Extract event data from HTML
// This is a simplified parser - adjust based on actual HTML structure
const eventPattern = /<div class="event-item"[^>]*>([\s\S]*?)<\/div>/gi;
let match;

while ((match = eventPattern.exec(html)) !== null) {
  const eventHtml = match[1];

  // Extract title
  const titleMatch = /<h[23][^>]*>([^<]+)<\/h[23]>/i.exec(eventHtml);
  const title = titleMatch ? titleMatch[1].trim() : '';

  // Extract date
  const dateMatch = /<time[^>]*datetime="([^"]+)"/i.exec(eventHtml);
  const date = dateMatch ? dateMatch[1] : '';

  if (title && date) {
    events.push({
      title: title,
      date: date,
      time: '',
      location: 'Beaumont',
      city: 'Beaumont',
      category: 'Community Event',
      description: title,
      source_url: 'https://www.beaumontcvb.com/events/'
    });
  }
}

return events.map(event => ({ json: event }));

// Parse Port Arthur Events
const html = $input.item.json.data;
const events = [];

const eventPattern = /<div class="event"[^>]*>([\s\S]*?)<\/div>/gi;
let match;

while ((match = eventPattern.exec(html)) !== null) {
  const eventHtml = match[1];

  const titleMatch = /<h[23][^>]*>([^<]+)<\/h[23]>/i.exec(eventHtml);
  const title = titleMatch ? titleMatch[1].trim() : '';

  if (title) {
    events.push({
      title: title,
      date: new Date().toISOString().split('T')[0],
      time: '',
      location: 'Port Arthur',
      city: 'Port Arthur',
      category: 'Cultural Event',
      description: title,
      source_url: 'https://visitportarthurtx.com/annual-events/'
    });
  }
}

return events.map(event => ({ json: event }));

// Parse Orange Events
const html = $input.item.json.data;
const events = [];

const eventPattern = /<tr[^>]*>([\s\S]*?)<\/tr>/gi;
let match;

while ((match = eventPattern.exec(html)) !== null) {
  const eventHtml = match[1];

  const titleMatch = /<td[^>]*>([^<]+)<\/td>/i.exec(eventHtml);
  const title = titleMatch ? titleMatch[1].trim() : '';

  const dateMatch = /<td[^>]*>([0-9\/]+)<\/td>/i.exec(eventHtml);
  const date = dateMatch ? dateMatch[1] : '';

  if (title && title.length > 3) {
    events.push({
      title: title,
      date: date,
      time: '',
      location: 'Orange Recreation Center',
      city: 'Orange',
      category: 'Sports & Recreation',
      description: title,
      source_url: 'https://orangetexas.gov/499/Upcoming-Events'
    });
  }
}

return events.map(event => ({ json: event }));
